'use strict';

const BasePoolConnection = require('mysql2/lib/base/pool_connection.js');

class PoolConnection extends BasePoolConnection {
  promise(promiseImpl) {
    const PromisePoolConnection = require('mysql2/lib/promise/pool_connection.js');
    return new PromisePoolConnection(this, promiseImpl);
  }
}

module.exports = PoolConnection;
